import logo from './logo.svg';
import './App.css';
import {useState} from 'react'

function App() {

  const [donorName, setDonorName] = useState("");
  const [amountDonated, setAmountDonated] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
      const response = await fetch("http://localhost:8080/api/donations", 
        {
          method: "POST",
          headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name: donorName, amountDonated: parseFloat(amountDonated)  }),
    });
      const data = await response.json();
      console.log(data)
    }
    catch(err){
      console.error("Error submitting data ", err)
    }

  }

  return (
    <div>
      <h1>Donations</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder='FirstName LastName'
          onChange={(e)=> setDonorName(e.target.value)} 
        />
        <input
          type="number"
          placeholder='Amount Donated'
          value={amountDonated}
          onChange={(e) => setAmountDonated(e.target.value)}
        />
        <button type="Submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
